tables.table_schema:
CREATE TABLE VendorPerformanceMetrics (
  vendor_id INT,
  vendor_name VARCHAR(255),
  performance_rating INT,
  performance_date DATE
);

CREATE TABLE vendor_performance_metrics (
    id INT PRIMARY KEY,
    vendor_name VARCHAR(255),
    delivery_performance INT,
    quality_performance INT
);

CREATE TABLE VendorMetrics (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR(255),
    total_orders INT,
    total_spent DECIMAL(10, 2)
);

CREATE TABLE OrderMetrics (
    order_id INT PRIMARY KEY,
    vendor_id INT,
    order_date DATE,
    order_total DECIMAL(10, 2)
);

CREATE TABLE SupplierMetrics (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    total_orders INT,
    average_lead_time INT
);

CREATE TABLE vendor_metrics (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR(255),
    performance_rating FLOAT
);

CREATE TABLE procurement_metrics (
    procurement_id INT PRIMARY KEY,
    vendor_id INT,
    delivery_time INT,
    satisfaction_rating FLOAT
);

CREATE TABLE supplier_metrics (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    vendor_id INT,
    performance_rating FLOAT
);

CREATE TABLE Vendor (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR(255) NOT NULL,
    vendor_address VARCHAR(255) NOT NULL
);

CREATE TABLE SupplierMetrics (
    supplier_id INT PRIMARY KEY,
    delivery_performance DECIMAL,
    quality_performance DECIMAL,
    communication_performance DECIMAL
);

CREATE TABLE SupplierOrders (
    order_id INT PRIMARY KEY,
    supplier_id INT,
    order_date DATE,
    total_amount DECIMAL
);

CREATE TABLE VendorRating (
    vendor_id INT,
    quality_rating DECIMAL,
    delivery_rating DECIMAL
);

CREATE TABLE ProcurementLogs (
    log_id INT PRIMARY KEY,
    event_type VARCHAR(50) NOT NULL,
    log_description TEXT,
    log_timestamp TIMESTAMP
);
