tables.table_schema:
CREATE TABLE procurement_reports (
    report_id SERIAL PRIMARY KEY,
    report_name VARCHAR(255) NOT NULL,
    total_savings DECIMAL NOT NULL,
    date_generated DATE
);

CREATE TABLE procurement_spending (
    spending_id SERIAL PRIMARY KEY,
    dept_name VARCHAR(255) NOT NULL,
    amount_spent DECIMAL NOT NULL,
    purchase_date DATE
);

CREATE TABLE ProcurementReports (
  id INT PRIMARY KEY,
  report_name VARCHAR(255),
  report_date DATE,
  total_spending DECIMAL
);

CREATE TABLE CostSavings (
  id INT PRIMARY KEY,
  saving_id INT,
  saving_amount DECIMAL,
  description TEXT
);

CREATE TABLE transactions (
   id UUID,
   date DATE,
   item_name VARCHAR,
   amount DECIMAL
);

CREATE TABLE categories (
   id UUID,
   category_name VARCHAR
);

CREATE TABLE savings (
   id UUID,
   date DATE,
   amount_saved DECIMAL
);
