tables.table_schema:
CREATE TABLE compliance_data (
  supplier_id INT,
  regulatory_requirement VARCHAR(255),
  compliance_level VARCHAR(50),
  compliance_date DATE
);

CREATE TABLE compliance_data (
    supplier_id INT PRIMARY KEY,
    supplier_name STRING,
    compliance_status BOOLEAN
);

CREATE TABLE regulatory_requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name STRING
);

CREATE TABLE supplier_compliance (
 supplier_id INT PRIMARY KEY,
 supplier_name VARCHAR(255),
 compliance_status BOOLEAN
);

CREATE TABLE regulatory_requirements (
 requirement_id INT PRIMARY KEY,
 requirement_name VARCHAR(255),
 requirement_description TEXT
);

CREATE TABLE supplier_regulatory_map (
 supplier_id INT,
 requirement_id INT,
 FOREIGN KEY (supplier_id) REFERENCES supplier_compliance(supplier_id),
 FOREIGN KEY (requirement_id) REFERENCES regulatory_requirements(requirement_id)
);

CREATE TABLE Suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    supplier_address VARCHAR(255),
    supplier_contact VARCHAR(50)
);

CREATE TABLE Certifications (
    certification_id INT PRIMARY KEY,
    certification_name VARCHAR(100),
    certification_agency VARCHAR(100)
);

CREATE TABLE SupplierCertifications (
    supplier_id INT REFERENCES Suppliers(supplier_id),
    certification_id INT REFERENCES Certifications(certification_id),
    certification_date DATE,
    PRIMARY KEY (supplier_id, certification_id)
);
