tables.table_schema:
CREATE TABLE seasonal_demand (
    product_id INT,
    season VARCHAR(255),
    demand INT
);

CREATE TABLE seasonal_demand ( 
    product_id INT PRIMARY KEY, 
    season VARCHAR(50), 
    demand INT 
);

CREATE TABLE inventory_levels ( 
    product_id INT PRIMARY KEY, 
    current_level INT, 
    optimal_level INT 
);

CREATE TABLE historic_demand ( 
    product_id INT, 
    month VARCHAR(20), 
    demand INT 
);

CREATE TABLE seasonal_demand_pattern (
	id INT,
	product_name VARCHAR(255),
	season VARCHAR(50),
	demand_level INT
);

CREATE TABLE inventory_levels (
	id INT,
	product_id INT,
	current_stock_level INT,
	optimal_stock_level INT
);

CREATE TABLE sales_data (
	sale_id INT,
	product_id INT,
	sale_date DATE,
	sale_quantity INT
);

CREATE TABLE product_details (
	product_id INT,
	product_name VARCHAR(255),
	product_category VARCHAR(100),
	supplier_id INT
);

CREATE TABLE SeasonalDemand (
  product_id INT PRIMARY KEY,
  season VARCHAR(50) NOT NULL,
  demand_level INT NOT NULL
);

CREATE TABLE InventoryAdjustment (
  adjustment_id INT PRIMARY KEY,
  product_id INT NOT NULL,
  adjustment_value INT NOT NULL
);

CREATE TABLE ProductSales (
  sale_id INT PRIMARY KEY,
  product_id INT NOT NULL,
  sale_date DATE NOT NULL,
  sale_amount INT NOT NULL
);

CREATE TABLE ProductInventory (
  product_id INT PRIMARY KEY,
  inventory_level INT NOT NULL,
  last_updated TIMESTAMP
);

CREATE TABLE InventoryLogs (
  log_id INT PRIMARY KEY,
  product_id INT NOT NULL,
  log_date DATE NOT NULL,
  log_description TEXT
);
