tables.table_schema:
CREATE TABLE historical_events (
  id SERIAL PRIMARY KEY,
  event_date DATE,
  event_description TEXT
);

CREATE TABLE external_factors (
  id SERIAL PRIMARY KEY,
  factor_name VARCHAR(50),
  factor_value DECIMAL(10, 2)
);

CREATE TABLE external_factors (
  factor_id UUID PRIMARY KEY,
  date DATE NOT NULL,
  factor_name TEXT,
  factor_description TEXT
);
