tables.table_schema:
CREATE TABLE slow_moving_inventory (
    item_id INT NOT NULL,
    item_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL,
    last_updated DATE
);

CREATE TABLE obsolete_inventory (
    item_id INT NOT NULL,
    item_name VARCHAR(255) NOT NULL,
    quantity INT NOT NULL,
    last_updated DATE
);

CREATE TABLE clearance_strategies (
    strategy_id INT NOT NULL,
    strategy_name VARCHAR(255),
    description TEXT,
    PRIMARY KEY (strategy_id)
);

CREATE TABLE slow_moving_inventory (
  product_id INT PRIMARY KEY,
  product_name VARCHAR (255),
  quantity INT,
  last_updated DATE
);

CREATE TABLE obsolete_inventory (
  product_id INT PRIMARY KEY,
  product_name VARCHAR (255),
  quantity INT,
  last_updated DATE
);

CREATE TABLE clearance_strategies (
  strategy_id INT PRIMARY KEY,
  strategy_name VARCHAR (255),
  description TEXT
);

CREATE TABLE inventory_reports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  inventory_status VARCHAR (255),
  clearance_strategy_id INT
);

CREATE TABLE clearance_strategies (
   strategy_id INT PRIMARY KEY,
   strategy_name VARCHAR(255),
   clearance_method VARCHAR(100),
   strategy_description TEXT
);

CREATE TABLE inventory_categories (
   category_id INT PRIMARY KEY,
   category_name VARCHAR(100)
);
