tables.table_schema:
CREATE TABLE historical_data (
    id INT PRIMARY KEY,
    date DATE,
    demand INT,
    inventory_level INT
);

CREATE TABLE inventory_replenishment (
    id INT PRIMARY KEY,
    date DATE,
    replenishment_qty INT,
    supplier_id INT
);

CREATE TABLE historical_sales (
    order_id INT PRIMARY KEY,
    date DATE NOT NULL,
    product_id INT NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE product_details (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    category VARCHAR(100) NOT NULL,
    price DECIMAL(10,2) NOT NULL
);

CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    quantity_in_stock INT NOT NULL,
    last_replenishment_date DATE NOT NULL
);

CREATE TABLE historical_demand_data (
    id SERIAL PRIMARY KEY,
    date DATE,
    demand INT
);

CREATE TABLE inventory_replenishment (
    id SERIAL PRIMARY KEY,
    product_id INT,
    replenishment_date DATE,
    quantity INT
);

CREATE TABLE product_information (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(50),
    unit_price DECIMAL
);

CREATE TABLE demand_forecast (
    id SERIAL PRIMARY KEY,
    product_id INT,
    forecast_date DATE,
    forecasted_demand INT
);

CREATE TABLE demand_forecasting (
  customer_id INT,
  product_id INT,
  demand_amount INT,
  forecast_date DATE
);

CREATE TABLE inventory_replenishment (
  product_id INT,
  replenishment_date DATE,
  replenishment_quantity INT
);

CREATE TABLE customer_data (
  customer_id INT,
  customer_name VARCHAR(50),
  customer_address VARCHAR(100),
  customer_phone VARCHAR(15)
);

CREATE TABLE product_data (
  product_id INT,
  product_name VARCHAR(50),
  product_category VARCHAR(50),
  product_price FLOAT
);
