tables.table_schema:
CREATE TABLE InventoryItems (
  id INT PRIMARY KEY,
  barcode VARCHAR(50) UNIQUE,
  item_name VARCHAR(100),
  stock_quantity INT
);

CREATE TABLE BarcodeScanningLogs (
  log_id INT PRIMARY KEY,
  scan_date TIMESTAMP,
  scanned_barcode VARCHAR(50),
  scanned_by_user VARCHAR(50)
);

CREATE TABLE inventory_logs (
  log_id UUID PRIMARY KEY,
  item_id UUID REFERENCES inventory_items(item_id),
  log_description VARCHAR(200) NOT NULL,
  log_date TIMESTAMP
);

CREATE TABLE barcode_scans (
  scan_id UUID PRIMARY KEY,
  barcode VARCHAR(50),
  scan_date TIMESTAMP
);

CREATE TABLE Inventory_Items (
  item_id INT PRIMARY KEY,
  item_name VARCHAR(255) NOT NULL,
  item_description TEXT,
  barcode_number VARCHAR(50) UNIQUE
);

CREATE TABLE Stock_Records (
  record_id INT PRIMARY KEY,
  item_id INT REFERENCES Inventory_Items(item_id),
  quantity INT,
  date_added TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE Barcode_Scans (
  scan_id INT PRIMARY KEY,
  barcode_number VARCHAR(50) REFERENCES Inventory_Items(barcode_number),
  scan_time TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

CREATE TABLE Users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50) UNIQUE,
  password_hash CHAR(64),
  role ENUM('admin', 'staff')
);
