tables.table_schema:
CREATE TABLE traffic_data (
    location_id INT,
    timestamp TIMESTAMP,
    vehicle_count INT,
    congestion_level VARCHAR(255)
);

CREATE TABLE CongestionPatterns (
  location VARCHAR,
  congestion_level INT,
  congestion_time TIME,
  suggested_action VARCHAR
);

CREATE TABLE congestion_patterns (
    id UUID PRIMARY KEY,
    location VARCHAR(255),
    date TIMESTAMP,
    congestion_level INT
);

CREATE TABLE traffic_incidents (
    incident_id UUID PRIMARY KEY,
    location VARCHAR(255),
    incident_type VARCHAR(50),
    incident_time TIMESTAMP
);

CREATE TABLE transportation_efficiency (
    route_id UUID PRIMARY KEY,
    route_name VARCHAR(100),
    average_speed FLOAT,
    traffic_lights_count INT
);

CREATE TABLE traffic_cameras (
    camera_id INT PRIMARY KEY,
    location VARCHAR,
    camera_type VARCHAR,
    status VARCHAR
);
