tables.table_schema:
CREATE TABLE inventory_counts (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    physical_count INT,
    recorded_count INT
);

CREATE TABLE inventory_discrepancies (
    id INT PRIMARY KEY,
    discrepancy_date DATE,
    product_name VARCHAR(255),
    discrepancy_reason TEXT
);

CREATE TABLE inventory_counts (
    id UUID PRIMARY KEY,
    item_name VARCHAR(255),
    physical_count INT,
    recorded_count INT
);

CREATE TABLE inventory_discrepancies (
    id UUID PRIMARY KEY,
    item_name VARCHAR(255),
    discrepancy_amount INT,
    discrepancy_reason TEXT
);

CREATE TABLE inventory_losses (
    id UUID PRIMARY KEY,
    item_name VARCHAR(255),
    loss_amount INT,
    investigation_status VARCHAR(50)
);

CREATE TABLE inventory_investigations (
    id UUID PRIMARY KEY,
    investigation_date TIMESTAMP,
    investigator_name VARCHAR(255),
    investigation_notes TEXT
);
