tables.table_schema:
CREATE TABLE carbon_emissions_data (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    emission_year INT,
    emission_value DECIMAL(10,2)
);

CREATE TABLE suppliers (
    id INT,
    name VARCHAR(255),
    location VARCHAR(255),
    carbon_emissions FLOAT
);

CREATE TABLE emissions_data (
    id INT,
    supplier_id INT,
    emissions_date DATE,
    emissions_amount FLOAT
);

CREATE TABLE suppliers (
   supplier_id INT PRIMARY KEY,
   supplier_name VARCHAR(50),
   country VARCHAR(50),
   emission_rate DECIMAL
);

CREATE TABLE emissions (
   emission_id INT PRIMARY KEY,
   supplier_id INT,
   emission_value DECIMAL,
   emission_date DATE
);

CREATE TABLE analysis (
   analysis_id INT PRIMARY KEY,
   emission_id INT,
   analysis_result VARCHAR(100),
   analysis_date DATE
);

CREATE TABLE Suppliers (
 supplier_id INT PRIMARY KEY,
 supplier_name VARCHAR(50),
 country VARCHAR(50),
 total_emissions DECIMAL(10,2)
);

CREATE TABLE EmissionCategories (
 category_id INT PRIMARY KEY,
 category_name VARCHAR(50)
);
