tables.table_schema:
CREATE TABLE sustainability_standards (
    supplier_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    compliance_status BOOL,
    last_audit_date DATE
);

CREATE TABLE Suppliers (
  supplier_id INT PRIMARY KEY,
  supplier_name VARCHAR(255),
  compliance_status BOOLEAN,
  sustainability_rating INT
);

CREATE TABLE Standards (
  standard_id INT PRIMARY KEY,
  standard_name VARCHAR(255),
  compliance_level BOOLEAN,
  description TEXT
);

CREATE TABLE Suppliers (
    supplier_id SERIAL PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    location VARCHAR(255) NOT NULL,
    sustainability_rating INT
);

CREATE TABLE SustainabilityStandards (
    standard_id SERIAL PRIMARY KEY,
    standard_name VARCHAR(255) NOT NULL,
    description TEXT,
    responsible_organization VARCHAR(255)
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_location VARCHAR(255) NOT NULL,
    sustainability_score INT
);

CREATE TABLE standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255) NOT NULL,
    standard_description TEXT NOT NULL
);

CREATE TABLE supplier_data (
    supplier_id INT PRIMARY KEY,
    supplier_name TEXT,
    compliance_status BOOL,
    sustainability_rating INT
);

CREATE TABLE compliance_records (
    record_id INT PRIMARY KEY,
    supplier_id INT,
    compliance_date DATE,
    compliance_description TEXT
);

CREATE TABLE standards (
    standard_id INT PRIMARY KEY,
    standard_name TEXT
);

CREATE TABLE supplier_standards (
    supplier_id INT,
    standard_id INT,
    PRIMARY KEY (supplier_id, standard_id)
);

CREATE TABLE contact_info (
    supplier_id INT PRIMARY KEY,
    contact_email TEXT,
    contact_phone TEXT
);
