tables.table_schema:
CREATE TABLE logistics_partners (
    partner_id INT PRIMARY KEY,
    partner_name VARCHAR(255),
    emissions_kg FLOAT
);

CREATE TABLE logistics_partner (
  partner_id UUID PRIMARY KEY,
  partner_name VARCHAR(255) NOT NULL,
  partner_location VARCHAR(255) NOT NULL
);

CREATE TABLE emissions_data (
  data_id UUID PRIMARY KEY,
  partner_id UUID REFERENCES logistics_partner(partner_id),
  emission_date DATE NOT NULL,
  emission_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE sustainability_report (
  report_id UUID PRIMARY KEY,
  partner_id UUID REFERENCES logistics_partner(partner_id),
  report_date DATE NOT NULL,
  report_description TEXT NOT NULL
);

CREATE TABLE logistics_partners (
    partner_id SERIAL PRIMARY KEY,
    partner_name VARCHAR(255) NOT NULL,
    partner_location VARCHAR(255) NOT NULL,
    emissions_data JSONB
);

CREATE TABLE emissions_data (
    id SERIAL PRIMARY KEY,
    partner_id INT NOT NULL,
    emission_date DATE NOT NULL,
    CO2_emission DECIMAL(10, 2) NOT NULL,
    NOx_emission DECIMAL(10, 2) NOT NULL
);

CREATE TABLE sustainability_reports (
    report_id SERIAL PRIMARY KEY,
    report_date DATE NOT NULL,
    report_content TEXT NOT NULL,
    partner_id INT NOT NULL
);

CREATE TABLE reports_history (
    history_id SERIAL PRIMARY KEY,
    report_id INT NOT NULL,
    partner_id INT NOT NULL,
    historical_data JSONB
);

CREATE TABLE logistics_partners (
    partner_id INT PRIMARY KEY,
    partner_name VARCHAR(255),
    partner_type VARCHAR(50),
    emissions_data DECIMAL(10,2)
);

CREATE TABLE transport_modes (
    mode_id INT PRIMARY KEY,
    mode_name VARCHAR(50),
    mode_description TEXT
);

CREATE TABLE emission_factors (
    factor_id INT PRIMARY KEY,
    factor_type VARCHAR(50),
    factor_value FLOAT
);

CREATE TABLE emissions_data (
    data_id INT PRIMARY KEY,
    partner_id INT,
    mode_id INT,
    factor_id INT,
    emission_amount DECIMAL(10,2),
    emission_date DATE,
    FOREIGN KEY (partner_id) REFERENCES logistics_partners(partner_id),
    FOREIGN KEY (mode_id) REFERENCES transport_modes(mode_id),
    FOREIGN KEY (factor_id) REFERENCES emission_factors(factor_id)
);

CREATE TABLE sustainability_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    total_emissions DECIMAL(12,2),
    report_notes TEXT
);
