tables.table_schema:
CREATE TABLE RecycleCenters (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255),
    capacity INT
);

CREATE TABLE RecyclingMaterials (
    id INT PRIMARY KEY,
    material_name VARCHAR(255),
    recycling_type VARCHAR(255),
    recycle_center_id INT,
    FOREIGN KEY (recycle_center_id) REFERENCES RecycleCenters(id)
);

CREATE TABLE monitoring_goods (
  id SERIAL PRIMARY KEY,
  product_name TEXT NOT NULL,
  quantity INT NOT NULL,
  location TEXT
);

CREATE TABLE recycling_centers (
  id SERIAL PRIMARY KEY,
  center_name TEXT NOT NULL,
  location TEXT NOT NULL,
  capacity INT
);

CREATE TABLE sorting_rules (
  id SERIAL PRIMARY KEY,
  rule_name TEXT NOT NULL,
  rule_description TEXT NOT NULL,
  rule_criteria TEXT
);

CREATE TABLE recycling_materials (
  id SERIAL PRIMARY KEY,
  material_name TEXT NOT NULL,
  material_type TEXT NOT NULL,
  recyclable BOOLEAN
);

CREATE TABLE circular_economy_strategies (
  id SERIAL PRIMARY KEY,
  strategy_name TEXT NOT NULL,
  description TEXT NOT NULL,
  implementation_date DATE
);
