tables.table_schema:
CREATE TABLE supplier_performance_metrics (
    supplier_id INT PRIMARY KEY,
    on_time_delivery DECIMAL,
    quality_ratings DECIMAL
);

CREATE TABLE supplier_orders (
    order_id INT PRIMARY KEY,
    supplier_id INT,
    order_date DATE,
    order_amount DECIMAL
);

CREATE TABLE SupplierMetrics (
    supplier_id INT,
    on_time_delivery_rating INT,
    quality_rating INT
);

CREATE TABLE DeliveryMetrics (
    delivery_id INT,
    supplier_id INT,
    delivery_date DATE,
    on_time BOOLEAN
);

CREATE TABLE QualityMetrics (
    quality_id INT,
    supplier_id INT,
    defect_count INT,
    inspection_date DATE
);

CREATE TABLE Supplier (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255) NOT NULL,
    on_time_delivery_rating INT,
    quality_rating INT
);

CREATE TABLE supplier_performance_metrics (
    supplier_id INT PRIMARY KEY,
    on_time_delivery_rating INT,
    quality_rating INT
);

CREATE TABLE supplier_contacts (
    supplier_id INT PRIMARY KEY,
    contact_name VARCHAR(50),
    email VARCHAR(50),
    phone_number VARCHAR(15)
);

CREATE TABLE supplier_ratings (
    rating_id INT PRIMARY KEY,
    supplier_id INT,
    overall_rating INT,
    feedback TEXT
);
