tables.table_schema:
CREATE TABLE supplier_inventory (
  supplier_id INT PRIMARY KEY,
  product_id INT,
  current_quantity INT,
  reorder_threshold INT
);

CREATE TABLE supplier ( 
	supplier_id INT PRIMARY KEY, 
	supplier_name VARCHAR(50) NOT NULL, 
	contact_person VARCHAR(50) NOT NULL, 
	email VARCHAR(100) NOT NULL 
);

CREATE TABLE product ( 
	product_id INT PRIMARY KEY, 
	product_name VARCHAR(50) NOT NULL, 
	unit_price DECIMAL(10, 2) NOT NULL, 
	quantity INT NOT NULL 
);

CREATE TABLE supplier (
    supplier_id UUID PRIMARY KEY,
    supplier_name VARCHAR(255),
    supplier_email VARCHAR(255),
    supplier_phone VARCHAR(15)
);

CREATE TABLE product (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255),
    product_price DECIMAL(10, 2),
    product_quantity INT
);

CREATE TABLE inventory (
    inventory_id UUID PRIMARY KEY,
    product_id UUID REFERENCES product(product_id),
    supplier_id UUID REFERENCES supplier(supplier_id),
    stock_level DECIMAL(10, 2)
);

CREATE TABLE reorder_threshold (
    threshold_id UUID PRIMARY KEY,
    product_id UUID REFERENCES product(product_id),
    min_stock_level DECIMAL(10, 2),
    reorder_quantity INT
);

CREATE TABLE reorder_alert (
    alert_id UUID PRIMARY KEY,
    threshold_id UUID REFERENCES reorder_threshold(threshold_id),
    alert_message TEXT,
    alert_datetime TIMESTAMP
);
