tables.table_schema:
CREATE TABLE returned_items (
  id INT PRIMARY KEY,
  item_name TEXT,
  condition TEXT
);

CREATE TABLE items_condition (
    item_id INT PRIMARY KEY,
    condition VARCHAR(255),
    return_date DATE
);

CREATE TABLE returned_items (
    item_id INT PRIMARY KEY,
    item_name VARCHAR(255) NOT NULL,
    condition VARCHAR(50) NOT NULL
);

CREATE TABLE refurbishment_decisions (
    decision_id INT PRIMARY KEY,
    item_id INT NOT NULL,
    refurbishment_date DATE NOT NULL,
    refurbishment_cost DECIMAL(10,2) NOT NULL
);

CREATE TABLE disposal_decisions (
    decision_id INT PRIMARY KEY,
    item_id INT NOT NULL,
    disposal_reason VARCHAR(255) NOT NULL,
    disposal_date DATE NOT NULL
);

CREATE TABLE refurbishment_logs (
    log_id INT PRIMARY KEY,
    item_id INT NOT NULL,
    refurbishment_action VARCHAR(255) NOT NULL,
    refurbishment_date DATE NOT NULL
);

CREATE TABLE disposal_logs (
    log_id INT PRIMARY KEY,
    item_id INT NOT NULL,
    disposal_action VARCHAR(255) NOT NULL,
    disposal_date DATE NOT NULL
);
