tables.table_schema:
CREATE TABLE inventory_metrics (
  product_id INT PRIMARY KEY,
  inventory_level INT,
  turnover_rate DECIMAL,
  profit_margin DECIMAL
);

CREATE TABLE inventory (
  id INT PRIMARY KEY,
  product_name VARCHAR(255),
  stock_level INT,
  inventory_value DECIMAL(10, 2)
);

CREATE TABLE inventory_turnover (
    product_id INT PRIMARY KEY,
    turnover_rate FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE stock_levels (
    product_id INT PRIMARY KEY,
    stock_quantity INT,
    location_id INT,
    last_updated TIMESTAMP
);

CREATE TABLE profit_margins (
    product_id INT PRIMARY KEY,
    profit_margin FLOAT,
    date TIMESTAMP
);
