tables.table_schema:
CREATE TABLE breach_analysis (
    analysis_id INT PRIMARY KEY,
    breach_id INT,
    analysis_date DATE,
    analysis_report TEXT
);

CREATE TABLE breach_mitigation (
    mitigation_id INT PRIMARY KEY,
    analysis_id INT,
    mitigation_date DATE,
    mitigation_plan TEXT
);

CREATE TABLE mitigation_logs (
    log_id INT PRIMARY KEY,
    mitigation_id INT,
    log_date DATE,
    log_message TEXT
);

CREATE TABLE security_breaches (
  id SERIAL PRIMARY KEY,
  breach_date DATE NOT NULL,
  type VARCHAR(50) NOT NULL,
  description TEXT NOT NULL
);

CREATE TABLE affected_companies (
  id SERIAL PRIMARY KEY,
  breach_id INT NOT NULL,
  company_name VARCHAR(100) NOT NULL,
  industry VARCHAR(50) NOT NULL
);

CREATE TABLE breach_impact (
  id SERIAL PRIMARY KEY,
  breach_id INT NOT NULL,
  financial_cost DECIMAL(10, 2) NOT NULL,
  data_records_affected INT NOT NULL
);

CREATE TABLE mitigation_strategies (
  id SERIAL PRIMARY KEY,
  breach_id INT NOT NULL,
  strategy_name VARCHAR(100) NOT NULL,
  description TEXT NOT NULL
);

CREATE TABLE breach_report (
  id SERIAL PRIMARY KEY,
  breach_id INT NOT NULL,
  report_date DATE NOT NULL,
  report_author VARCHAR(100) NOT NULL
);
