tables.table_schema:
CREATE TABLE RiskIndicators (
  id SERIAL PRIMARY KEY,
  risk_name VARCHAR(255) NOT NULL,
  risk_value DECIMAL NOT NULL
);

CREATE TABLE DashboardViews (
  view_id SERIAL PRIMARY KEY,
  view_name VARCHAR(255) NOT NULL,
  view_type VARCHAR(50) NOT NULL,
  created_date TIMESTAMP
);

CREATE TABLE risk_dashboard (
    id SERIAL PRIMARY KEY,
    risk_indicator VARCHAR(100) NOT NULL,
    indicator_value FLOAT NOT NULL,
    timestamp TIMESTAMPTZ NOT NULL
);

CREATE TABLE user_accounts (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE dashboard_preferences (
    user_id SERIAL PRIMARY KEY,
    show_risk_threshold BOOLEAN NOT NULL,
    show_alerts BOOLEAN NOT NULL,
    alert_emails VARCHAR(100)[]
);

CREATE TABLE risk_dashboard (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  value DECIMAL,
  timestamp TIMESTAMP
);

CREATE TABLE risk_category (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(255),
  description TEXT
);

CREATE TABLE risk_metric (
  metric_id INT PRIMARY KEY,
  metric_name VARCHAR(255),
  weight DECIMAL,
  target DECIMAL
);

CREATE TABLE dashboard_user (
  user_id INT PRIMARY KEY,
  username VARCHAR(255),
  email VARCHAR(255),
  role VARCHAR(50)
);

CREATE TABLE metric_assignment (
  assignment_id INT PRIMARY KEY,
  metric_id INT,
  user_id INT,
  assignment_date DATE
);
