tables.table_schema:
CREATE TABLE financial_instruments (
	symbol STRING,
	quantity INT,
	price FLOAT
);

CREATE TABLE risk_factors (
	risk_factor_id INT,
	factor_name STRING,
	volatility FLOAT,
	correlation FLOAT
);

CREATE TABLE var_results (
	result_id INT,
	instrument_id INT,
	risk_factor_id INT,
	var_value FLOAT
);

CREATE TABLE VAR_Calculation (
    instrument_id INT,
    date DATE,
    var_value DECIMAL
);

CREATE TABLE Instruments (
    instrument_id INT,
    instrument_name VARCHAR,
    asset_class VARCHAR
);

CREATE TABLE Risk_Periods (
    period_id INT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Risk_Values (
    instrument_id INT,
    period_id INT,
    risk_value DECIMAL
);

CREATE TABLE instruments (
    id INT PRIMARY KEY,
    name STRING,
    type STRING,
    quantity INT
);

CREATE TABLE portfolios (
    id INT PRIMARY KEY,
    name STRING,
    manager_id INT
);

CREATE TABLE prices (
    id INT PRIMARY KEY,
    instrument_id INT,
    price FLOAT
);

CREATE TABLE market_data (
    date DATE,
    instrument_id INT,
    opening_price FLOAT,
    closing_price FLOAT
);

CREATE TABLE var_results (
    id INT PRIMARY KEY,
    portfolio_id INT,
    date DATE,
    vaR FLOAT
);
