tables.table_schema:
CREATE TABLE risk_assessment_reports ( 
 report_id INTEGER PRIMARY KEY, 
 report_title VARCHAR(255), 
 report_date DATE, 
 report_content TEXT 
 );

CREATE TABLE risk_assessment_reports (
    report_id INT PRIMARY KEY,
    report_name VARCHAR(255) NOT NULL,
    report_date DATE,
    risk_level VARCHAR(50),
    INDEX (report_date)
);

CREATE TABLE decision_makers (
    decision_maker_id INT PRIMARY KEY,
    decision_maker_name VARCHAR(255) NOT NULL,
    decision_maker_role VARCHAR(100)
);

CREATE TABLE assessment_comments (
    comment_id INT PRIMARY KEY,
    report_id INT,
    comment_text TEXT,
    comment_date TIMESTAMP,
    INDEX (report_id)
);

CREATE TABLE users ( 
  user_id INT PRIMARY KEY, 
  user_name VARCHAR(50), 
  user_email VARCHAR(100) UNIQUE 
);

CREATE TABLE assessment_status ( 
  status_id INT PRIMARY KEY, 
  status_name VARCHAR(50) 
);

CREATE TABLE access_control ( 
  access_id INT PRIMARY KEY, 
  user_id INT REFERENCES users(user_id), 
  report_id INT REFERENCES risk_assessment_reports(id), 
  status_id INT REFERENCES assessment_status(status_id) 
);
