tables.table_schema:
CREATE TABLE market_trends (
    trend_id SERIAL PRIMARY KEY,
    trend_description TEXT,
    trend_date DATE
);

CREATE TABLE risk_exposure (
    exposure_id SERIAL PRIMARY KEY,
    exposure_description TEXT,
    exposure_category TEXT,
    exposure_amount DECIMAL
);

CREATE TABLE market_data (
    data_id SERIAL PRIMARY KEY,
    data_type TEXT,
    data_value DECIMAL,
    data_date DATE
);

CREATE TABLE MarketData (
  id INT PRIMARY KEY,
  symbol VARCHAR(50),
  price DECIMAL(10, 2),
  timestamp TIMESTAMP
);

CREATE TABLE RiskExposure (
  exposure_id INT PRIMARY KEY,
  symbol VARCHAR(50),
  exposure_amount DECIMAL(10, 2),
  risk_level VARCHAR(50)
);

CREATE TABLE RiskAssessment (
  assessment_id INT PRIMARY KEY,
  symbol VARCHAR(50),
  risk_score DECIMAL(10, 2),
  risk_comment TEXT
);
