tables.table_schema:
CREATE TABLE RiskFactors (
    id INT PRIMARY KEY,
    risk_factor_name VARCHAR(50) NOT NULL,
    description TEXT,
    weight FLOAT
);

CREATE TABLE risk_factors (
  factor_id INT PRIMARY KEY,
  factor_name VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE dependencies (
  dependency_id INT PRIMARY KEY,
  factor_id INT,
  description TEXT,
  FOREIGN KEY (factor_id) REFERENCES risk_factors(factor_id)
);

CREATE TABLE RiskFactors (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    description TEXT
);

CREATE TABLE RiskDependencies (
    id INT PRIMARY KEY,
    dependency_name VARCHAR(255),
    dependent_factor_id INT,
    FOREIGN KEY (dependent_factor_id) REFERENCES RiskFactors(id)
);

CREATE TABLE RiskType (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(255)
);

CREATE TABLE risk_factors (
  id INT PRIMARY KEY,
  factor_name VARCHAR(255)
);

CREATE TABLE dependencies (
  id INT PRIMARY KEY,
  factor_id INT,
  dependent_factor_id INT
);

CREATE TABLE factor_types (
  id INT PRIMARY KEY,
  type_name VARCHAR(255)
);

CREATE TABLE factor_mapping (
  id INT PRIMARY KEY,
  factor_id INT,
  type_id INT
);

CREATE TABLE risk_assessment (
  id INT PRIMARY KEY,
  factor_id INT,
  assessment_score INT
);
