tables.table_schema:
CREATE TABLE network_slicing (
	service_id INT PRIMARY KEY,
	service_name VARCHAR(255),
	slice_type VARCHAR(50),
	resource_allocation INT
);

CREATE TABLE network_slices (
  slice_id UUID PRIMARY KEY,
  slice_name TEXT,
  service_type TEXT,
  resource_limit INT
);

CREATE TABLE service_resources (
  resource_id UUID PRIMARY KEY,
  slice_id UUID REFERENCES network_slices(slice_id),
  resource_type TEXT,
  allocated_resource INT
);

CREATE TABLE network_slicing_resources (
    resource_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    resource_type VARCHAR(50),
    resource_capacity INT
);

CREATE TABLE service_requirements (
    service_id INT PRIMARY KEY,
    service_name VARCHAR(50),
    required_resources INT,
    priority_level INT
);

CREATE TABLE resource_allocation (
    allocation_id INT PRIMARY KEY,
    resource_id INT,
    service_id INT,
    allocated_capacity INT
);

CREATE TABLE network_slice (
    slice_id INT PRIMARY KEY,
    slice_name VARCHAR(255) NOT NULL,
    service_type VARCHAR(50) NOT NULL
);

CREATE TABLE resource_allocation (
    allocation_id INT PRIMARY KEY,
    slice_id INT,
    resource_type VARCHAR(100) NOT NULL,
    allocated_amount INT NOT NULL
);

CREATE TABLE service_mapping (
    service_id INT PRIMARY KEY,
    slice_id INT,
    service_name VARCHAR(255) NOT NULL
);

CREATE TABLE user_subscription (
    user_id INT PRIMARY KEY,
    slice_id INT,
    subscription_date DATE NOT NULL,
    expiration_date DATE NOT NULL
);
