tables.table_schema:
CREATE TABLE IoT_Sensor_Data (
  sensor_id INT PRIMARY KEY,
  sensor_name VARCHAR(50) NOT NULL,
  network_traffic INT NOT NULL,
  data_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE network_traffic_sensor_data (
    id UUID PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    data_type STRING,
    data_value FLOAT
);

CREATE TABLE sensor_metadata (
    sensor_id INT PRIMARY KEY,
    location STRING,
    status BOOLEAN
);

CREATE TABLE sensor_health (
    id UUID PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    status BOOLEAN,
    message STRING
);

CREATE TABLE traffic_analysis (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    source_ip STRING,
    dest_ip STRING,
    protocol STRING,
    data_size INT
);

CREATE TABLE alert_logs (
    id UUID PRIMARY KEY,
    timestamp TIMESTAMP,
    alert_type STRING,
    description STRING
);
