tables.table_schema:
CREATE TABLE NetworkPerformance ( 
  network_id INT PRIMARY KEY, 
  network_name VARCHAR(50), 
  network_type VARCHAR(20), 
  network_capacity INT 
);

CREATE TABLE network_devices (
    id INT PRIMARY KEY,
    device_name VARCHAR(50),
    device_type VARCHAR(20),
    device_location VARCHAR(50)
);

CREATE TABLE user_devices (
    device_id INT PRIMARY KEY,
    user_id INT REFERENCES users(user_id),
    device_name VARCHAR(20),
    device_type VARCHAR(10)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(50),
    user_email VARCHAR(50)
);

CREATE TABLE network_issues (
    issue_id INT PRIMARY KEY,
    call_id INT REFERENCES call_records(call_id),
    issue_category VARCHAR(50),
    issue_description VARCHAR(100)
);
