tables.table_schema:
CREATE TABLE network_congestion_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    data_volume INT NOT NULL,
    congestion_level VARCHAR(50) NOT NULL
);

CREATE TABLE network_traffic_data (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    data_usage FLOAT,
    congestion_level INT
);

CREATE TABLE resource_allocation (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    allocated_memory FLOAT,
    allocated_bandwidth FLOAT
);

CREATE TABLE congestion_prediction (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    predicted_congestion_level INT,
    optimization_strategy VARCHAR(255)
);

CREATE TABLE historical_data (
    id INT PRIMARY KEY,
    date DATE,
    network_traffic INT,
    latency INT
);

CREATE TABLE congestion_prediction (
    id INT PRIMARY KEY,
    date DATE,
    predicted_congestion INT,
    resource_allocated VARCHAR
);

CREATE TABLE resource_optimization (
    id INT PRIMARY KEY,
    date DATE,
    optimized_resource_allocation VARCHAR
);

CREATE TABLE network_devices (
    id INT PRIMARY KEY,
    device_name VARCHAR,
    device_type VARCHAR,
    location VARCHAR
);

CREATE TABLE historical_data (
  record_id INT PRIMARY KEY,
  date TIMESTAMP,
  network_usage FLOAT,
  resource_allocation FLOAT
);

CREATE TABLE network_traffic (
  traffic_id INT PRIMARY KEY,
  device_id INT,
  timestamp TIMESTAMP,
  bytes_transferred FLOAT
);

CREATE TABLE congestion_predictions (
  prediction_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  predicted_congestion VARCHAR
);

CREATE TABLE resource_optimization (
  optimization_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  optimized_resources VARCHAR
);
