tables.table_schema:
CREATE TABLE network_latency (
    latency_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    latency_ms DECIMAL
);

CREATE TABLE packet_loss (
    loss_id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    loss_percentage DECIMAL
);

CREATE TABLE network_latency_data (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  latency_ms FLOAT,
  packet_loss_pct FLOAT
);

CREATE TABLE connectivity_summary (
  id INT PRIMARY KEY,
  date DATE,
  total_latency_ms FLOAT,
  avg_packet_loss_pct FLOAT
);

CREATE TABLE issue_resolution (
  id INT PRIMARY KEY,
  issue_id INT,
  resolution TEXT,
  resolved_by VARCHAR(50)
);
