tables.table_schema:
CREATE TABLE network_outages (
  outage_id INT PRIMARY KEY,
  outage_date DATE,
  outage_duration INT,
  affected_service VARCHAR
);

CREATE TABLE service_disruptions (
  disruption_id INT PRIMARY KEY,
  disruption_date DATE,
  service_affected VARCHAR,
  cause TEXT
);

CREATE TABLE service_disruptions (
  id UUID PRIMARY KEY,
  start_time TIMESTAMP,
  end_time TIMESTAMP,
  description TEXT
);

CREATE TABLE outage_reports (
  id UUID PRIMARY KEY,
  outage_id UUID REFERENCES network_outages(id),
  reporter_name VARCHAR(100),
  report_date DATE
);

CREATE TABLE regulatory_requirements (
    id INT PRIMARY KEY,
    requirement_name VARCHAR(255),
    description TEXT,
    deadline DATE
);
