tables.table_schema:
CREATE TABLE regulatory_safety_standards (
  standard_id INT PRIMARY KEY,
  standard_name VARCHAR(50),
  description TEXT
);

CREATE TABLE RegulatoryStandards (
	id INT PRIMARY KEY,
	standard_id VARCHAR(255),
	description TEXT
);

CREATE TABLE SafetyReports (
	id INT PRIMARY KEY,
	equipment_id INT,
	report_date DATE,
	report_description TEXT
);
