tables.table_schema:
CREATE TABLE telecom_infrastructure (
  id INTEGER PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  status VARCHAR(50) NOT NULL
);

CREATE TABLE telecom_locations (
 location_id UUID PRIMARY KEY,
 latitude FLOAT,
 longitude FLOAT,
 address VARCHAR(255)
);

CREATE TABLE telecom_status (
 status_id UUID PRIMARY KEY,
 location_id UUID,
 status VARCHAR(50),
 update_time TIMESTAMP
);

CREATE TABLE regulatory_compliance (
 compliance_id UUID PRIMARY KEY,
 location_id UUID,
 regulations TEXT,
 compliance_status VARCHAR(50)
);

CREATE TABLE telecom_towers (
    id INT PRIMARY KEY,
    location VARCHAR(255),
    status VARCHAR(50)
);

CREATE TABLE antennas (
    id INT PRIMARY KEY,
    tower_id INT,
    type VARCHAR(50),
    height INT
);

CREATE TABLE regulations (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    description TEXT
);

CREATE TABLE inspections (
    id INT PRIMARY KEY,
    tower_id INT,
    inspector VARCHAR(100),
    inspection_date DATE
);
