tables.table_schema:
CREATE TABLE subscriber_consent (
    subscriber_id INT PRIMARY KEY,
    consent_status VARCHAR(50),
    collection_date DATE,
    consent_description TEXT
);

CREATE TABLE consent_log (
    log_id SERIAL PRIMARY KEY,
    subscriber_id INT,
    consent_status VARCHAR(50),
    log_timestamp TIMESTAMP
);

CREATE TABLE consent_records (
    id SERIAL PRIMARY KEY,
    subscriber_id INT NOT NULL,
    consent_type VARCHAR(50) NOT NULL,
    consent_date DATE
);

CREATE TABLE SubscriberConsent (
    subscriber_id INT PRIMARY KEY,
    consent_date DATE,
    consent_given BOOLEAN
);

CREATE TABLE DataRetention (
    retention_id INT PRIMARY KEY,
    data_id INT,
    retention_period INT,
    FOREIGN KEY (data_id) REFERENCES DataCollection(data_id)
);

CREATE TABLE subscriber_consent (
    subscriber_id INT PRIMARY KEY,
    consent_date DATE,
    data_collection_allowed BOOLEAN,
    data_usage_allowed BOOLEAN
);

CREATE TABLE subscriber_location (
    subscriber_id INT PRIMARY KEY,
    country VARCHAR(50),
    city VARCHAR(50),
    postal_code VARCHAR(15)
);

CREATE TABLE data_collection_logs (
    log_id INT PRIMARY KEY,
    subscriber_id INT,
    collection_date TIMESTAMP,
    data_collected TEXT
);

CREATE TABLE data_usage_logs (
    log_id INT PRIMARY KEY,
    subscriber_id INT,
    usage_date TIMESTAMP,
    data_used TEXT
);
