tables.table_schema:
CREATE TABLE regulatory_audits (
  audit_id INT PRIMARY KEY,
  audit_date DATE,
  audit_type VARCHAR(50),
  audit_result VARCHAR(100)
);

CREATE TABLE regulatory_inspections (
  inspection_id INT PRIMARY KEY,
  inspection_date DATE,
  inspected_entity VARCHAR(100),
  inspection_result VARCHAR(100)
);

CREATE TABLE telecommunications_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT
);

CREATE TABLE Audit (
  audit_id INT PRIMARY KEY,
  audit_date DATE,
  audited_entity VARCHAR(255),
  compliance_rating INT
);

CREATE TABLE Inspection (
  inspection_id INT PRIMARY KEY,
  inspection_date DATE,
  inspected_entity VARCHAR(255),
  findings TEXT
);

CREATE TABLE RegulatoryBody (
  body_id INT PRIMARY KEY,
  body_name VARCHAR(255),
  jurisdiction VARCHAR(100)
);

CREATE TABLE audits (
    audit_id INT PRIMARY KEY,
    audit_date DATE,
    audit_type VARCHAR(50),
    audit_status VARCHAR(50)
);

CREATE TABLE inspections (
    inspection_id INT PRIMARY KEY,
    inspection_date DATE,
    inspector_name VARCHAR(50),
    inspection_result VARCHAR(100)
);

CREATE TABLE regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name VARCHAR(100),
    regulation_description TEXT
);

CREATE TABLE violations (
    violation_id INT PRIMARY KEY,
    violation_type VARCHAR(50),
    violation_date DATE,
    violation_description TEXT
);

CREATE TABLE compliance_records (
    record_id INT PRIMARY KEY,
    record_date DATE,
    record_details TEXT,
    compliance_status VARCHAR(50)
);
