CREATE TABLE service_providers (
	id UUID,
	provider_name VARCHAR(255),
	permit_number VARCHAR(50),
	PRIMARY KEY (id)
);

CREATE TABLE authorized_telecom_operators (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    license_number VARCHAR(50)
);

CREATE TABLE service_providers (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    permit_number VARCHAR(50)
);

CREATE TABLE regulations (
    id INT PRIMARY KEY,
    description TEXT,
    effective_date DATE
);

CREATE TABLE authorized_telecom_operators (
    operator_id INT PRIMARY KEY,
    operator_name VARCHAR(50) NOT NULL,
    permit_id INT NOT NULL,
    license_expiry DATE
);

CREATE TABLE authorized_service_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(50) NOT NULL,
    permit_id INT NOT NULL,
    license_expiry DATE
);

CREATE TABLE permits (
    permit_id INT PRIMARY KEY,
    permit_type VARCHAR(50) NOT NULL,
    issue_date DATE NOT NULL,
    expiry_date DATE NOT NULL
);

CREATE TABLE licenses (
    license_id INT PRIMARY KEY,
    license_type VARCHAR(50) NOT NULL,
    valid_from DATE NOT NULL,
    valid_to DATE
);

CREATE TABLE authorized_telecom_operators (
    operator_id INT PRIMARY KEY,
    operator_name VARCHAR(255) NOT NULL,
    permit_number VARCHAR(50) UNIQUE,
    permit_expiry_date DATE
);

CREATE TABLE service_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(255) NOT NULL,
    registration_number VARCHAR(50) UNIQUE
);

CREATE TABLE operator_services (
    service_id INT PRIMARY KEY,
    operator_id INT,
    service_type VARCHAR(100) NOT NULL,
    FOREIGN KEY(operator_id) REFERENCES authorized_telecom_operators(operator_id)
);

CREATE TABLE provider_services (
    service_id INT PRIMARY KEY,
    provider_id INT,
    service_type VARCHAR(100) NOT NULL,
    FOREIGN KEY(provider_id) REFERENCES service_providers(provider_id)
);
