tables.table_schema:
CREATE TABLE compliance_data (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    regulation_type VARCHAR(100),
    compliance_status BOOLEAN
);

CREATE TABLE industry_standards (
    standard_id INT PRIMARY KEY,
    standard_name VARCHAR(255),
    description TEXT,
    published_date DATE
);

CREATE TABLE regulatory_agencies (
   id INT PRIMARY KEY,
   agency_name VARCHAR(50) NOT NULL,
   contact_email VARCHAR(50)
);

CREATE TABLE standards (
   id INT PRIMARY KEY,
   standard_code VARCHAR(10) NOT NULL,
   description TEXT
);

CREATE TABLE companies (
   id INT PRIMARY KEY,
   company_name VARCHAR(50) NOT NULL,
   industry VARCHAR(50),
   location VARCHAR(100)
);
