tables.table_schema:
CREATE TABLE CallPatterns (
  pattern_id INT PRIMARY KEY,
  pattern_name VARCHAR(50),
  call_frequency INT,
  average_call_duration INT
);

CREATE TABLE CallPatterns (
  id INT PRIMARY KEY,
  phone_number VARCHAR(15),
  call_count INT,
  total_call_duration INTERVAL
);

CREATE TABLE Billing (
    bill_id INT PRIMARY KEY,
    cdr_id INT REFERENCES CDRs(cdr_id),
    total_amount DECIMAL(10, 2),
    payment_status VARCHAR(10)
);

CREATE TABLE billing (
    id INT PRIMARY KEY,
    cdr_id INT,
    amount DECIMAL,
    payment_status VARCHAR(20)
);

CREATE TABLE customers (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    address VARCHAR(100),
    phone_number VARCHAR(12)
);

CREATE TABLE call_logs (
    id INT PRIMARY KEY,
    cdr_id INT,
    logged_at TIMESTAMP,
    call_type VARCHAR(20)
);

CREATE TABLE analysis (
    id INT PRIMARY KEY,
    cdr_id INT,
    caller_location VARCHAR(50),
    receiver_location VARCHAR(50)
);
