tables.table_schema:
CREATE TABLE network_coverage (
    id INT PRIMARY KEY,
    location GEOMETRY,
    coverage_area FLOAT
);

CREATE TABLE infrastructure_planning (
    id INT PRIMARY KEY,
    location GEOMETRY,
    planned_infrastructure VARCHAR
);

CREATE TABLE network_coverage (
    id INT PRIMARY KEY,
    area_name VARCHAR,
    coverage_range FLOAT,
    coverage_status BOOLEAN
);

CREATE TABLE network_infrastructure (
    id INT PRIMARY KEY,
    city_name VARCHAR,
    infrastructure_type VARCHAR,
    equipment_count INT
);

CREATE TABLE network_coverage (
    coverage_id INT PRIMARY KEY,
    area_name VARCHAR(50),
    coverage_type VARCHAR(50),
    coverage_range INT
);

CREATE TABLE coverage_area (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50),
  area_type VARCHAR(50),
  coverage_geometry GEOMETRY
);

CREATE TABLE network_nodes (
  node_id INT PRIMARY KEY,
  node_name VARCHAR(50),
  location_point POINT,
  is_active BOOLEAN DEFAULT TRUE
);

CREATE TABLE network_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  node_id INT,
  device_type VARCHAR(50)
);
