tables.table_schema:
CREATE TABLE network_performance_metrics (
    metric_id INT PRIMARY KEY,
    latency_ms FLOAT,
    bandwidth_usage_mb FLOAT,
    packet_loss DECIMAL
);

CREATE TABLE analysis_data (
    analysis_id INT PRIMARY KEY,
    metric_id INT,
    analysis_result TEXT,
    FOREIGN KEY (metric_id) REFERENCES network_performance_metrics(metric_id)
);

CREATE TABLE NetworkLatencyMetrics (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    latency_ms FLOAT
);

CREATE TABLE BandwidthUsageMetrics (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    bandwidth_usage_mbps FLOAT
);

CREATE TABLE PacketLossMetrics (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    packet_loss_percent FLOAT
);

CREATE TABLE network_metrics (
  id INT PRIMARY KEY,
  latency_ms INT,
  bandwidth_usage_mb INT,
  packet_loss DECIMAL
);

CREATE TABLE latency_history (
  timestamp TIMESTAMP PRIMARY KEY,
  latency_ms INT
);

CREATE TABLE bandwidth_history (
  timestamp TIMESTAMP PRIMARY KEY,
  bandwidth_usage_mb INT
);

CREATE TABLE packet_loss_history (
  timestamp TIMESTAMP PRIMARY KEY,
  packet_loss DECIMAL
);

CREATE TABLE network_latency_metrics (
    id INT PRIMARY KEY,
    latency_ms FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE bandwidth_usage_metrics (
    id INT PRIMARY KEY,
    bandwidth_usage_mb FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE packet_loss_metrics (
    id INT PRIMARY KEY,
    packet_loss_percent FLOAT,
    timestamp TIMESTAMP
);
