tables.table_schema:
CREATE TABLE software_licenses (
    id SERIAL PRIMARY KEY,
    license_key VARCHAR(255) NOT NULL,
    software_name VARCHAR(255) NOT NULL,
    company_name VARCHAR(255) NOT NULL
);

CREATE TABLE license_usage (
    id SERIAL PRIMARY KEY,
    license_key VARCHAR(255) NOT NULL,
    user_id INT NOT NULL,
    usage_time TIMESTAMP NOT NULL
);

CREATE TABLE license_compliance (
    id SERIAL PRIMARY KEY,
    license_key VARCHAR(255) NOT NULL,
    is_compliant BOOLEAN NOT NULL
);

CREATE TABLE software_usage (
    id SERIAL PRIMARY KEY,
    license_id INT NOT NULL,
    usage_date DATE,
    used_hours INT
);

CREATE TABLE customer (
    id SERIAL PRIMARY KEY,
    customer_name TEXT NOT NULL,
    contact_email TEXT,
    company_name TEXT
);

CREATE TABLE license_cost (
    license_id INT NOT NULL,
    cost DECIMAL(10, 2),
    currency TEXT
);

CREATE TABLE CostOptimization (
    optimization_id INT PRIMARY KEY,
    license_id INT,
    optimization_description TEXT,
    cost_saved DECIMAL(10,2)
);
