tables.table_schema:
CREATE TABLE WirelessProtocols (
  protocol_id INT PRIMARY KEY,
  protocol_name VARCHAR(50) NOT NULL,
  frequency FLOAT,
  bandwidth FLOAT
);

CREATE TABLE NetworkDevices (
  device_id SERIAL PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL,
  protocol_id INT REFERENCES WirelessProtocols(protocol_id),
  ip_address VARCHAR(15)
);

CREATE TABLE wifi_networks (
  network_id INT PRIMARY KEY,
  ssid VARCHAR(50) NOT NULL,
  password VARCHAR(50) NOT NULL
);

CREATE TABLE bluetooth_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL,
  pairing_code VARCHAR(10) NOT NULL
);

CREATE TABLE zigbee_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL,
  endpoint INT NOT NULL
);

CREATE TABLE lora_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50) NOT NULL,
  frequency_band VARCHAR(20) NOT NULL
);
