tables.table_schema:
CREATE TABLE subscribers (
  id INT PRIMARY KEY,
  phone_number VARCHAR(10),
  full_name VARCHAR(50),
  address VARCHAR(100)
);

CREATE TABLE sim_cards (
  sim_id INT PRIMARY KEY,
  subscriber_id INT,
  sim_number VARCHAR(10),
  activation_status BOOLEAN
);

CREATE TABLE subscriber_identity_info (
  id INT PRIMARY KEY,
  phone_number VARCHAR(10),
  subscriber_name VARCHAR(50),
  address VARCHAR(100)
);

CREATE TABLE sim_card_info (
  sim_card_id INT PRIMARY KEY,
  iccid VARCHAR(20),
  imsi VARCHAR(15),
  status VARCHAR(10)
);

CREATE TABLE device (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  sim_id INT,
  FOREIGN KEY (sim_id) REFERENCES sim_card(sim_id)
);
