tables.table_schema:
CREATE TABLE coverage_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(255) NOT NULL,
  signal_strength INT
);

CREATE TABLE network_coverage_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  signal_strength INT
);

CREATE TABLE network_coverage_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  signal_strength INT
);

CREATE TABLE signal_towers (
  tower_id INT PRIMARY KEY,
  tower_name VARCHAR(50) NOT NULL,
  area_id INT,
  FOREIGN KEY (area_id) REFERENCES network_coverage_areas(area_id)
);

CREATE TABLE network_providers (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(50) NOT NULL
);

CREATE TABLE coverage_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  signal_strength FLOAT NOT NULL,
  coverage_type ENUM('indoor', 'outdoor')
);

CREATE TABLE network_providers (
  provider_id INT PRIMARY KEY,
  provider_name VARCHAR(50) NOT NULL,
  provider_type ENUM('3G', '4G', '5G')
);
