tables.table_schema:
CREATE TABLE satellite_images (
  image_id INT PRIMARY KEY,
  image_name VARCHAR(255),
  image_timestamp TIMESTAMP,
  image_data BYTEA
);

CREATE TABLE image_tags (
  tag_id INT PRIMARY KEY,
  tag_name VARCHAR(50)
);

CREATE TABLE SatelliteImages (
    image_id INT,
    image_name VARCHAR(255),
    capture_date DATE,
    image_data BYTEA
);

CREATE TABLE ImageMetadata (
    metadata_id INT,
    image_id INT,
    metadata_key VARCHAR(50),
    metadata_value VARCHAR(255)
);

CREATE TABLE ImageAnalysisResults (
    analysis_id INT,
    image_id INT,
    analysis_date DATE,
    analysis_result JSONB
);

CREATE TABLE image_analysis_results (
    analysis_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES satellite_images(id),
    analyzed_date DATE NOT NULL,
    analysis_result TEXT
);

CREATE TABLE image_visualization (
    visualization_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES satellite_images(id),
    visualization_type VARCHAR(50) NOT NULL,
    visualization_url TEXT NOT NULL
);

CREATE TABLE satellite_observations (
    observation_id SERIAL PRIMARY KEY,
    image_id INT REFERENCES satellite_images(id),
    observation_date DATE NOT NULL,
    observation_details TEXT
);
