tables.table_schema:
CREATE TABLE satellite_communication (
    satellite_id INT PRIMARY KEY,
    frequency FLOAT,
    bandwidth FLOAT
);

CREATE TABLE satellite_comms ( 
    id INT PRIMARY KEY, 
    satellite_name VARCHAR(255), 
    frequency_band VARCHAR(50), 
    bandwidth_allocations INT 
);

CREATE TABLE frequency_band ( 
    band_id INT PRIMARY KEY, 
    band_name VARCHAR(50) 
);
