tables.table_schema:
CREATE TABLE satellites (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);
CREATE TABLE orbits (
    id INT PRIMARY KEY,
    satellite_id INT,
    orbit_type VARCHAR(100),
    altitude FLOAT
);
CREATE TABLE positions (
    id INT PRIMARY KEY,
    satellite_id INT,
    timestamp TIMESTAMP,
    position_x FLOAT,
    position_y FLOAT,
    position_z FLOAT
);

CREATE TABLE satellites (
    satellite_id UUID PRIMARY KEY,
    satellite_name TEXT,
    orbit_type TEXT,
    altitude_km DOUBLE PRECISION
);

CREATE TABLE orbits (
    orbit_id UUID PRIMARY KEY,
    orbit_name TEXT,
    period_minutes DOUBLE PRECISION
);

CREATE TABLE tracking_history (
    tracking_id UUID PRIMARY KEY,
    satellite_id UUID REFERENCES satellites(satellite_id),
    position_id UUID REFERENCES positions(position_id),
    timestamp TIMESTAMP
);

CREATE TABLE satellites (
  satellite_id INT PRIMARY KEY,
  satellite_name TEXT,
  orbit_radius FLOAT,
  orbit_speed FLOAT
);

CREATE TABLE satellite_telemetry (
  telemetry_id SERIAL PRIMARY KEY,
  satellite_id INT,
  timestamp TIMESTAMP,
  telemetry_data JSON
);
