tables.table_schema:
CREATE TABLE satellite_coverage_areas (
    id INT PRIMARY KEY,
    area_name STRING,
    coverage_range FLOAT
);

CREATE TABLE signal_strength (
    id INT PRIMARY KEY,
    satellite_id INT,
    signal_strength FLOAT
);

CREATE TABLE coverage_areas (
  satellite_id INT,
  coverage_area VARCHAR(255),
  signal_strength INT
);

CREATE TABLE satellite_details (
  satellite_id INT,
  satellite_name VARCHAR(255),
  launch_date DATE,
  manufacturer VARCHAR(255)
);

CREATE TABLE signal_strength_history (
  satellite_id INT,
  timestamp DATETIME,
  signal_strength INT
);

CREATE TABLE SatelliteCoverageArea (
  coverage_area_id INT,
  satellite_id INT,
  coverage_area_name VARCHAR(50),
  signal_strength DECIMAL
);

CREATE TABLE Satellite (
  satellite_id INT,
  satellite_name VARCHAR(50),
  launch_date DATE
);

CREATE TABLE CommunicationSignal (
  signal_id INT,
  satellite_id INT,
  signal_strength DECIMAL,
  signal_time TIMESTAMP
);

CREATE TABLE Location (
  location_id INT,
  coverage_area_id INT,
  latitude DECIMAL(10, 6),
  longitude DECIMAL(10, 6)
);

CREATE TABLE AreaCoverage (
    satellite_id INT,
    coverage_area VARCHAR(50),
    signal_strength FLOAT
);

CREATE TABLE SignalStrength (
    satellite_id INT,
    signal_id INT,
    azimuth_angle FLOAT,
    elevation_angle FLOAT
);

CREATE TABLE SatellitePosition (
    satellite_id INT,
    longitude FLOAT,
    latitude FLOAT
);

CREATE TABLE CommunicationLogs (
    log_id INT,
    timestamp TIMESTAMP,
    signal_strength FLOAT,
    communication_type VARCHAR(50)
);

CREATE TABLE SatelliteDetails (
    satellite_id INT,
    satellite_name VARCHAR(50),
    operator VARCHAR(50),
    launch_date DATE
);
