tables.table_schema:
CREATE TABLE subscription_plans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(50) NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE billing_information (
    billing_id SERIAL PRIMARY KEY,
    plan_id INT REFERENCES subscription_plans(plan_id),
    subscriber_name VARCHAR(100) NOT NULL,
    billing_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Satellite_Communication_Subscription_Plans (
    plan_id INT PRIMARY KEY,
    plan_name VARCHAR(255),
    price DECIMAL(10,2)
);

CREATE TABLE subscription_plans (
    id UUID PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE subscribers (
    subscriber_id UUID PRIMARY KEY,
    plan_id UUID REFERENCES subscription_plans(id),
    activation_date DATE NOT NULL,
    status VARCHAR(10) NOT NULL
);

CREATE TABLE billing_information (
    bill_id UUID PRIMARY KEY,
    subscriber_id UUID REFERENCES subscribers(subscriber_id),
    amount DECIMAL(10, 2) NOT NULL,
    payment_status VARCHAR(10) NOT NULL
);

CREATE TABLE communication_history (
    communication_id UUID PRIMARY KEY,
    subscriber_id UUID REFERENCES subscribers(subscriber_id),
    communication_date DATE NOT NULL,
    message TEXT
);

CREATE TABLE SatelliteCommunicationPlans (
    plan_id UUID PRIMARY KEY,
    plan_name VARCHAR(100) NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE SubscriberAccounts (
    account_id UUID PRIMARY KEY,
    account_name VARCHAR(100) NOT NULL,
    email VARCHAR(255) NOT NULL,
    phone_number VARCHAR(20)
);

CREATE TABLE BillingInformation (
    bill_id UUID PRIMARY KEY,
    account_id UUID NOT NULL,
    bill_amount DECIMAL(10, 2) NOT NULL,
    payment_status VARCHAR(50) NOT NULL
);

CREATE TABLE SubscriptionHistory (
    history_id UUID PRIMARY KEY,
    account_id UUID NOT NULL,
    plan_id UUID NOT NULL,
    subscription_date TIMESTAMPTZ NOT NULL
);

CREATE TABLE SatelliteCommunicationUsage (
    usage_id UUID PRIMARY KEY,
    account_id UUID NOT NULL,
    data_usage DECIMAL(10, 2) NOT NULL,
    last_usage_date TIMESTAMPTZ NOT NULL
);
