tables.table_schema:
CREATE TABLE switch_mapping (
    id INT PRIMARY KEY,
    switch_name VARCHAR(50) NOT NULL,
    interface_id INT NOT NULL,
    port_number INT NOT NULL
);

CREATE TABLE NetworkSettings (
    setting_id INT PRIMARY KEY,
    setting_name VARCHAR(50),
    setting_value VARCHAR(50),
    description TEXT
);

CREATE TABLE NetworkLogs (
    log_id INT PRIMARY KEY,
    device_id INT,
    log_time TIMESTAMP,
    log_message TEXT
);

CREATE TABLE NetworkDevices (
    device_id INT PRIMARY KEY,
    device_name VARCHAR(50),
    ip_address VARCHAR(15),
    mac_address VARCHAR(17)
);

CREATE TABLE VLANConfiguration (
    vlan_id INT PRIMARY KEY,
    vlan_name VARCHAR(50),
    subnet VARCHAR(15),
    gateway VARCHAR(15)
);
