tables.table_schema:
CREATE TABLE telecommunication_towers (
 tower_id INT PRIMARY KEY,
 tower_name VARCHAR(100),
 location VARCHAR(255),
 equipment_count INT
);

CREATE TABLE TelecommunicationTowers (
    tower_id INT PRIMARY KEY,
    tower_name VARCHAR(255) NOT NULL,
    location_lat DECIMAL(10, 6) NOT NULL,
    location_lng DECIMAL(10, 6) NOT NULL
);

CREATE TABLE Equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name VARCHAR(255) NOT NULL,
    tower_id INT,
    FOREIGN KEY (tower_id) REFERENCES TelecommunicationTowers(tower_id)
);

CREATE TABLE Tower (
    tower_id INT PRIMARY KEY,
    tower_name TEXT,
    location_lat DOUBLE PRECISION,
    location_long DOUBLE PRECISION
);

CREATE TABLE Equipment (
    equipment_id INT PRIMARY KEY,
    equipment_name TEXT,
    tower_id INT,
    FOREIGN KEY (tower_id) REFERENCES Tower(tower_id)
);

CREATE TABLE Maintenance (
    maintenance_id INT PRIMARY KEY,
    equipment_id INT,
    maintenance_date DATE,
    description TEXT,
    FOREIGN KEY (equipment_id) REFERENCES Equipment(equipment_id)
);

CREATE TABLE NetworkOptimization (
    optimization_id INT PRIMARY KEY,
    equipment_id INT,
    optimization_date DATE,
    optimization_details TEXT,
    FOREIGN KEY (equipment_id) REFERENCES Equipment(equipment_id)
);

CREATE TABLE TelecommunicationTowers (
    tower_id INT PRIMARY KEY,
    tower_name VARCHAR(50) NOT NULL,
    location_lat DOUBLE PRECISION NOT NULL,
    location_long DOUBLE PRECISION NOT NULL
);

CREATE TABLE Technicians (
    technician_id INT PRIMARY KEY,
    technician_name VARCHAR(50) NOT NULL,
    technician_role VARCHAR(50) NOT NULL
);
